package com.poonam.doitimport android.content.Contextimport com.poonam.doit.di.component.DaggerAppComponentimport dagger.android.AndroidInjectorimport dagger.android.DaggerApplicationclass DoItApplication : DaggerApplication(){    init {        instance = this    }    companion object {        private var instance: DoItApplication? = null        fun applicationContext(): Context {            return instance!!.applicationContext        }    }    override fun applicationInjector(): AndroidInjector<out DaggerApplication> {        return DaggerAppComponent            .builder()            .application(this@DoItApplication)            .build()    }}