package com.poonam.doitimport androidx.appcompat.app.AppCompatActivityimport android.os.Bundleimport androidx.lifecycle.Observerimport androidx.lifecycle.ViewModelProviderimport androidx.lifecycle.ViewModelProvidersimport com.poonam.doit.di.module.ApiModuleimport dagger.Providesimport dagger.android.AndroidInjectionimport dagger.android.support.DaggerAppCompatActivityimport kotlinx.android.synthetic.main.activity_main.*import javax.inject.Injectclass MainActivity : DaggerAppCompatActivity() {    @Inject    internal lateinit var viewModelFactory : ViewModelProvider.Factory    val mainViewModel = ViewModelProviders.of(this,viewModelFactory).get(MainViewModel::class.java)    override fun onCreate(savedInstanceState: Bundle?) {        AndroidInjection.inject(this)        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_main)        mainViewModel.count.observe(this, Observer {            tvCount.text=it.toString()        })        btnCount.setOnClickListener {            mainViewModel.incrementCount()        }    }}