package com.poonam.doit.di.componentimport android.app.Applicationimport com.poonam.doit.DoItApplicationimport com.poonam.doit.di.module.ActivityModuleimport com.poonam.doit.di.module.ApiModuleimport com.poonam.doit.di.module.ContextModuleimport com.poonam.doit.di.module.ViewModelModuleimport dagger.BindsInstanceimport dagger.Componentimport dagger.android.AndroidInjectionModuleimport dagger.android.AndroidInjectorimport dagger.android.support.AndroidSupportInjectionModuleimport javax.inject.Singleton@Singleton@Component(    modules =    [        ActivityModule::class,        ContextModule::class,        ApiModule::class,        ViewModelModule::class,        AndroidSupportInjectionModule::class    ])interface AppComponent : AndroidInjector<DoItApplication> {    @Component.Builder    interface Builder {        @BindsInstance        fun application(application: Application): Builder        fun build(): AppComponent    }}